actions.trigger:
// Define rules that describes if the packet should trigger the Action. 
function shouldTriggerFromPacket(socketIndex, packet, objects) {
    return false;
}

// Define rules that describes if the specific time should trigger the Action. 
function shouldTriggerFromTime(timeSinceStartup) {
    return timeSinceStartup % 2 === 10; // Will run every 10th second
}

rules.update:
// Define rules that describes if the packet creates or updates an object. 
function isUpdateObjectRequest(socketIndex, packet) {
    if (socketIndex !== 0) {
        return false;
    }

    return packet.t === "USER_DATA";
}

rules.map:
// Define how the object will look like.
// Objects must always contain "_id" property
function mapObject(packet) {
    return {
        _id: packet.d.i,
        name: packet.d.n,
        level: packet.d.l
    };
}
